// Problem 662: Maximum Width of Binary Tree
// FIXME: Complete this challenge.

/**
 * Constraints:
 *
 * 1) The number of nodes in the tree is in the range [1, 3000].
 * 2) -100 <= Node.val <= 100.
 *
 */

/**
 * Definition for a binary tree node.
 * class TreeNode {
 *     val: number
 *     left: TreeNode | null
 *     right: TreeNode | null
 *     constructor(val?: number, left?: TreeNode | null, right?: TreeNode | null) {
 *         this.val = (val===undefined ? 0 : val)
 *         this.left = (left===undefined ? null : left)
 *         this.right = (right===undefined ? null : right)
 *     }
 * }
 */

class TreeNode {
	val: number;
	left: TreeNode | null;
	right: TreeNode | null;
	constructor(val?: number, left?: TreeNode | null, right?: TreeNode | null) {
		this.val = val === undefined ? 0 : val;
		this.left = left === undefined ? null : left;
		this.right = right === undefined ? null : right;
	}
}

/** A class for storing tree nodes and their associated level in a btree. */
class TreeNodeData {
	node: TreeNode | null;
	level: number;
	excludedNodes: number;
	constructor(node: TreeNode | null, level: number, excludedNodes: number) {
		this.node = node;
		this.level = level;
		this.excludedNodes = excludedNodes;
	}
}

function widthOfBinaryTree(root: TreeNode | null): number {
	let level: number = 1; // the current level in the btree we're assessing.
	let lNode: TreeNode | null = root; // the left-most end-node at the given level.
	let rNode: TreeNode | null = root; // the right-most end-node at the given level.
	let lExcludedNodes: number = 0; // the number of nodes to the left of the left-most node at the current level.
	let rExcludedNodes: number = 0; // the number of nodes to the right of the right-most node at the current level.
	let lParents: TreeNodeData[] = []; // a stack of ancestor nodes to `lNode` with untraversed children.
	let rParents: TreeNodeData[] = []; // a stack of ancestor nodes to `rNode` with untraversed children.
	let mWidth: number = 1; // tracks the largest width encountered as analysis continue.

	// We initialize `lNode` and `rNode` to the deepest level with a width equal
	// to what it would be in a complete btree. And, add any nodes we find with
	// untraversed children down to that level to the stacks `lParents` and
	// `rParents`.
	while (lNode?.left && rNode?.right) {
		if (hasBothChildren(lNode))
			lParents.push(new TreeNodeData(lNode, level, 0));
		if (hasBothChildren(rNode))
			rParents.push(new TreeNodeData(rNode, level, 0));
		lNode = lNode.left;
		rNode = rNode.right;
		level += 1;
	}
	mWidth = maxWidthOfLevel(level, lExcludedNodes, rExcludedNodes);

	// Now either `lNode` doesn't have a left child or `rNode` doesn't have a
	// right child. With `lNode` and `rNode` initialized, we advance `lNode` to
	// the next left-most node in `level += 1` and `rNode` to the next right-most
	// node in `level += 1`. Then, we calculate the width at `level += 1`. The
	// procession of this cycle stops when...
	let lNodeLevel: number;
	let rNodeLevel: number;
	while (lNode !== rNode || lNode.left || lNode.right) {
		// Move `lNode` to the next left-most node in `level += 1`;
		// *************************************************************************
		lNodeLevel = level;
		while (
			(lNode != rNode || lNode.left || lNode.right) &&
			lNodeLevel < level + 1
		) {
			// When `lNode` has both children.
			if (hasBothChildren(lNode)) {
				lParents.push(new TreeNodeData(lNode, lNodeLevel, lExcludedNodes));
				lNode = lNode.left;
				lNodeLevel += 1;
				lExcludedNodes *= 2;
			}
			// When `lNode` has only the left child.
			else if (lNode.left) {
				lNode = lNode.left;
				lNodeLevel += 1;
				lExcludedNodes *= 2;
			}
			// When `lNode` has only the right child.
			else if (lNode.right) {
				lNode = lNode.right;
				lNodeLevel += 1;
				lExcludedNodes = 2 * lExcludedNodes + 1;
			}
			// When `lNode` has no children.
			else {
				// Set `lNode` to the last ancestor node with an untraversed child and
				// `lNodeLevel` to the level of that node.
				lNodeLevel = lParents.at(-1).level + 1;
				lExcludedNodes = 2 * lParents.at(-1).excludedNodes + 1;
				lNode = lParents.pop().node.right;
			}
		}

		// Move `rNode` to the next right-most node in `level += 1`;
		// *************************************************************************
		rNodeLevel = level;
		while (
			(rNode != lNode || rNode.right || rNode.left) &&
			rNodeLevel < level + 1
		) {
			// When `rNode` has both children.
			if (hasBothChildren(rNode)) {
				rParents.push(new TreeNodeData(rNode, rNodeLevel, rExcludedNodes));
				rNode = rNode.right;
				rNodeLevel += 1;
				rExcludedNodes *= 2;
			}
			// When `rNode` has only the right child.
			else if (rNode.right) {
				rNode = rNode.right;
				rNodeLevel += 1;
				rExcludedNodes *= 2;
			}
			// When `rNode` has only the left child.
			else if (rNode.left) {
				rNode = rNode.left;
				rNodeLevel += 1;
				rExcludedNodes = 2 * rExcludedNodes + 1;
			}
			// When `lNode` has no children.
			else {
				// Set `rNode` to the last ancestor node with an untraversed child and
				// `rNodeLevel` to the level of that node.
				rNodeLevel = rParents.at(-1).level + 1;
				rExcludedNodes = 2 * rParents.at(-1).excludedNodes + 1;
				rNode = rParents.pop().node.left;
			}
		}
		// Both `lNode` and `rNode` reached the next nodes desired in `level += 1`,
		// so set the current level to `level += 1`.
		// if (lNode !== rNode) {
		if (lNode !== rNode || lNode.left || lNode.right) {
			level += 1;
			mWidth = Math.max(
				mWidth,
				maxWidthOfLevel(level, lExcludedNodes, rExcludedNodes)
			);
		}
		// }
	}

	return mWidth;

	// ************************

	function maxWidthOfLevel(
		level: number,
		lExcludedNodes: number,
		rExcludedNodes: number
	): number {
		if (
			!(level >= 0 && Number.isInteger(level)) ||
			!(lExcludedNodes >= 0 && Number.isInteger(lExcludedNodes)) ||
			!(rExcludedNodes >= 0 && Number.isInteger(rExcludedNodes))
		)
			throw Error();
		return Math.pow(2, level - 1) - (lExcludedNodes + rExcludedNodes);
	}

	/** Checks if a tree node has 2 children. */
	function hasBothChildren(tNode: TreeNode | null): boolean {
		if (tNode && tNode.left && tNode.right) return true;
		return false;
	}
}

